#!/bin/bash

if [[ "$OSTYPE" == "linux-gnu" ]]; then
    python_local="/home/$USER/.local/lib/python2.7/site-packages"
    redpy_path="/home/$USER/redpy"
    cd "/home/$USER"
elif [[ "$OSTYPE" == "msys" ]]; then
    python_local="C:/Users/$USERNAME/AppData/Local/Continuum/anaconda2/Lib/site-packages"
    redpy_path="C:/Users/$USERNAME/redpy"
    cd "C:/Users/$USERNAME"
fi

if [ -d "$python_local/bi_db" ]; then
  mkdir -p "$python_local/bi_db"
fi

cp "$redpy_path/biz_intel_creds.py" "$python_local/biz_intel_creds.py"
cp -TRv "$redpy_path" "$python_local/bi_db"

echo "redpy installation is complete"
